<script setup>
import imageSrc from '/assets/Images/coding.png'; // Importing from assets
import { onMounted, ref } from 'vue'
import { gsap } from 'gsap'
import { InertiaPlugin } from 'gsap/InertiaPlugin'
import { useAnimations } from "@/composables/useAnimations";

useAnimations();
const { $splitting } = useNuxtApp();

onMounted(() => {
  $splitting();
});


// Register GSAP plugin on the client
if (process.client) {
  gsap.registerPlugin(InertiaPlugin)
}

const root = ref(null)

onMounted(() => {

  let oldX = 0,
    oldY = 0,
    deltaX = 0,
    deltaY = 0

  if (!root.value) return

  root.value.addEventListener('mousemove', (e) => {
    deltaX = e.clientX - oldX
    deltaY = e.clientY - oldY
    oldX = e.clientX
    oldY = e.clientY
  })

  root.value.querySelectorAll('.media').forEach(el => {
    el.addEventListener('mouseenter', () => {
      const tl = gsap.timeline({
        onComplete: () => tl.kill()
      })

      tl.timeScale(1.2)

      const image = el.querySelector('.svg-icon')

      tl.to(image, {
        inertia: {
          x: {
            velocity: deltaX * 30,
            end: 0
          },
          y: {
            velocity: deltaY * 30,
            end: 0
          }
        }
      })

      tl.fromTo(image, {
        rotate: 0
      }, {
        duration: 0.4,
        rotate: (Math.random() - 0.5) * 30,
        yoyo: true,
        repeat: 1,
        ease: 'power1.inOut'
      }, '<')
    })
  })
  $splitting();




})
</script>
